import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
  useParams,
} from "react-router-dom";

const sampleBooks = [
  { id: 1, title: "The Odyssey", author: "Homer" },
  { id: 2, title: "Hamlet", author: "William Shakespeare" },
];

function Layout({ children }) {
  return (
    <div className="flex h-screen bg-gray-100">
      <aside className="w-64 bg-blue-800 text-white p-4 space-y-4">
        <h1 className="text-2xl font-bold">Library</h1>
        <nav className="space-y-2">
          <a href="/" className="block hover:bg-blue-600 rounded px-2 py-1">Home</a>
          <a href="/add-book" className="block hover:bg-blue-600 rounded px-2 py-1">Add Book</a>
        </nav>
      </aside>
      <main className="flex-1 p-6 overflow-y-auto">{children}</main>
    </div>
  );
}

function HomePage({ userRole, books, setBooks }) {
  const navigate = useNavigate();

  const borrowBook = (id) => alert(`Borrowed book ID ${id}`);
  const purchaseBook = (id) => alert(`Purchased book ID ${id}`);
  const returnBook = (id) => alert(`Returned book ID ${id}`);

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Welcome, {userRole}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {books.map((book) => (
          <div key={book.id} className="bg-white p-4 rounded shadow">
            <h3 className="font-bold">{book.title}</h3>
            <p className="text-gray-600">{book.author}</p>
            <div className="mt-2 space-x-2">
              {userRole === "admin" ? (
                <>
                  <button onClick={() => navigate(`/edit-book/${book.id}`)} className="bg-yellow-400 px-3 py-1 rounded text-white">Edit</button>
                  <button onClick={() => setBooks(books.filter((b) => b.id !== book.id))} className="bg-red-500 px-3 py-1 rounded text-white">Delete</button>
                </>
              ) : (
                <>
                  <button onClick={() => borrowBook(book.id)} className="bg-blue-500 px-3 py-1 rounded text-white">Borrow</button>
                  <button onClick={() => purchaseBook(book.id)} className="bg-green-500 px-3 py-1 rounded text-white">Purchase</button>
                  <button onClick={() => returnBook(book.id)} className="bg-yellow-500 px-3 py-1 rounded text-white">Return</button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function AddBookPage({ books, setBooks }) {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const navigate = useNavigate();

  const addBook = () => {
    const newBook = { id: Date.now(), title, author };
    setBooks([...books, newBook]);
    navigate("/");
  };

  return (
    <div className="max-w-md space-y-2">
      <h2 className="text-xl font-semibold mb-2">Add a Book</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" className="w-full p-2 border rounded" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} placeholder="Author" className="w-full p-2 border rounded" />
      <button onClick={addBook} className="bg-blue-600 text-white px-4 py-2 rounded">Add Book</button>
    </div>
  );
}

function EditBookPage({ books, setBooks }) {
  const { id } = useParams();
  const book = books.find((b) => b.id === parseInt(id));
  const [title, setTitle] = useState(book?.title || "");
  const [author, setAuthor] = useState(book?.author || "");
  const navigate = useNavigate();

  const updateBook = () => {
    setBooks(books.map((b) => (b.id === book.id ? { ...b, title, author } : b)));
    navigate("/");
  };

  return (
    <div className="max-w-md space-y-2">
      <h2 className="text-xl font-semibold mb-2">Edit Book</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} className="w-full p-2 border rounded" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} className="w-full p-2 border rounded" />
      <button onClick={updateBook} className="bg-yellow-600 text-white px-4 py-2 rounded">Update Book</button>
    </div>
  );
}

function LoginPage({ setUserRole }) {
  const navigate = useNavigate();

  const login = (role) => {
    setUserRole(role);
    navigate("/");
  };

  return (
    <div className="h-screen flex flex-col justify-center items-center space-y-4">
      <h2 className="text-3xl font-bold">Login</h2>
      <button onClick={() => login("admin")} className="bg-blue-600 text-white px-6 py-2 rounded">Login as Admin</button>
      <button onClick={() => login("user")} className="bg-green-600 text-white px-6 py-2 rounded">Login as User</button>
    </div>
  );
}

function LogoutButton({ setUserRole }) {
  const navigate = useNavigate();
  return (
    <button
      onClick={() => {
        setUserRole(null);
        navigate("/login");
      }}
      className="bg-red-600 text-white px-4 py-2 rounded mt-6"
    >
      Logout
    </button>
  );
}

export default function App() {
  const [userRole, setUserRole] = useState(null);
  const [books, setBooks] = useState(sampleBooks);

  return (
    <Router>
      {!userRole ? (
        <Routes>
          <Route path="*" element={<LoginPage setUserRole={setUserRole} />} />
        </Routes>
      ) : (
        <Layout>
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <HomePage userRole={userRole} books={books} setBooks={setBooks} />
                  <LogoutButton setUserRole={setUserRole} />
                </>
              }
            />
            {userRole === "admin" && (
              <>
                <Route path="/add-book" element={<AddBookPage books={books} setBooks={setBooks} />} />
                <Route path="/edit-book/:id" element={<EditBookPage books={books} setBooks={setBooks} />} />
              </>
            )}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </Layout>
      )}
    </Router>
  );
}
