// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';

function LoginPage({ setRole }) {
  const navigate = useNavigate();

  const handleLogin = (role) => {
    setRole(role);
    navigate('/home');
  };

  return (
    <div className="container text-center mt-5">
      <h2>Select Role</h2>
      <button className="btn btn-primary m-2" onClick={() => handleLogin('admin')}>Admin</button>
      <button className="btn btn-success m-2" onClick={() => handleLogin('user')}>User</button>
    </div>
  );
}

function Home({ role }) {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', genre: '' });
  const [editBookId, setEditBookId] = useState(null);
  const navigate = useNavigate();

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  const handleLogout = () => {
    navigate('/');
  };

  const addBook = async () => {
    await axios.post('http://localhost:5000/books', newBook);
    setNewBook({ title: '', author: '', genre: '' });
    fetchBooks();
  };

  const updateBook = async (id) => {
    await axios.put(`http://localhost:5000/books/${id}`, newBook);
    setEditBookId(null);
    setNewBook({ title: '', author: '', genre: '' });
    fetchBooks();
  };

  const deleteBook = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  const borrowBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/borrow`);
    fetchBooks();
  };

  const buyBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/buy`);
    fetchBooks();
  };

  const returnBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/return`);
    fetchBooks();
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-3">Welcome, {role}</h2>
      <button className="btn btn-danger mb-3" onClick={handleLogout}>Log Out</button>

      {role === 'admin' && (
        <div className="mb-4">
          <h4>{editBookId ? 'Edit Book' : 'Add Book'}</h4>
          <input className="form-control mb-2" placeholder="Title" value={newBook.title} onChange={e => setNewBook({ ...newBook, title: e.target.value })} />
          <input className="form-control mb-2" placeholder="Author" value={newBook.author} onChange={e => setNewBook({ ...newBook, author: e.target.value })} />
          <input className="form-control mb-2" placeholder="Genre" value={newBook.genre} onChange={e => setNewBook({ ...newBook, genre: e.target.value })} />
          <button className="btn btn-primary" onClick={editBookId ? () => updateBook(editBookId) : addBook}>
            {editBookId ? 'Update Book' : 'Add Book'}
          </button>
        </div>
      )}

      <h4>Available Books</h4>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.genre}</td>
              <td>{book.status}</td>
              <td>
                {role === 'admin' && (
                  <>
                    <button className="btn btn-sm btn-info me-1" onClick={() => { setNewBook(book); setEditBookId(book.id); }}>Edit</button>
                    <button className="btn btn-sm btn-danger me-1" onClick={() => deleteBook(book.id)}>Delete</button>
                  </>
                )}
                {role === 'user' && (
                  <>
                    <button className="btn btn-sm btn-warning me-1" disabled={book.status !== 'available'} onClick={() => borrowBook(book.id)}>Borrow</button>
                    <button className="btn btn-sm btn-success me-1" onClick={() => buyBook(book.id)}>Buy</button>
                    <button className="btn btn-sm btn-secondary" disabled={book.status !== 'borrowed'} onClick={() => returnBook(book.id)}>Return</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function App() {
  const [role, setRole] = useState(null);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setRole={setRole} />} />
        <Route path="/home" element={<Home role={role} />} />
      </Routes>
    </Router>
  );
}
