// frontend/src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './index.css';

function App() {
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = () => {
    axios.get('http://localhost:5000/books')
      .then(res => setBooks(res.data))
      .catch(err => console.error(err));
  };

  const handleAddOrUpdateBook = () => {
    if (title && author && genre) {
      if (editingId) {
        axios.put(`http://localhost:5000/books/${editingId}`, { title, author, genre })
          .then(() => {
            fetchBooks();
            resetForm();
          })
          .catch(err => console.error(err));
      } else {
        axios.post('http://localhost:5000/books', { title, author, genre })
          .then(() => {
            fetchBooks();
            resetForm();
          })
          .catch(err => console.error(err));
      }
    }
  };

  const handleDelete = id => {
    axios.delete(`http://localhost:5000/books/${id}`)
      .then(() => fetchBooks())
      .catch(err => console.error(err));
  };

  const handleBorrow = id => {
    axios.patch(`http://localhost:5000/books/${id}/borrow`)
      .then(() => fetchBooks())
      .catch(err => console.error(err));
  };

  const handleReturn = id => {
    axios.patch(`http://localhost:5000/books/${id}/return`)
      .then(() => fetchBooks())
      .catch(err => console.error(err));
  };

  const handleBuy = id => {
    axios.patch(`http://localhost:5000/books/${id}/buy`)
      .then(() => fetchBooks())
      .catch(err => console.error(err));
  };

  const handleEdit = book => {
    setTitle(book.title);
    setAuthor(book.author);
    setGenre(book.genre);
    setEditingId(book.id);
  };

  const resetForm = () => {
    setTitle('');
    setAuthor('');
    setGenre('');
    setEditingId(null);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Library Management System</h1>
        <button onClick={handleLogout} className="bg-red-600 px-4 py-2 rounded hover:bg-red-700">Logout</button>
      </div>
      <div className="mb-6 grid grid-cols-1 md:grid-cols-3 gap-4">
        <input className="p-2 rounded text-black" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
        <input className="p-2 rounded text-black" placeholder="Author" value={author} onChange={e => setAuthor(e.target.value)} />
        <input className="p-2 rounded text-black" placeholder="Genre" value={genre} onChange={e => setGenre(e.target.value)} />
        <button onClick={handleAddOrUpdateBook} className="col-span-1 md:col-span-3 bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded">
          {editingId ? 'Update Book' : 'Add Book'}
        </button>
      </div>
      <div className="grid gap-4">
        {books.map(book => (
          <div key={book.id} className="bg-gray-800 p-4 rounded shadow-md flex flex-col md:flex-row justify-between items-center">
            <div className="mb-2 md:mb-0">
              <p className="font-semibold text-lg">{book.title}</p>
              <p className="text-sm">{book.author} - {book.genre} ({book.status})</p>
            </div>
            <div className="flex gap-2 flex-wrap">
              <button onClick={() => handleEdit(book)} className="bg-yellow-500 hover:bg-yellow-600 px-3 py-1 rounded">Edit</button>
              <button onClick={() => handleDelete(book.id)} className="bg-red-500 hover:bg-red-600 px-3 py-1 rounded">Delete</button>
              <button onClick={() => handleBorrow(book.id)} className="bg-blue-500 hover:bg-blue-600 px-3 py-1 rounded">Borrow</button>
              <button onClick={() => handleReturn(book.id)} className="bg-green-500 hover:bg-green-600 px-3 py-1 rounded">Return</button>
              <button onClick={() => handleBuy(book.id)} className="bg-purple-500 hover:bg-purple-600 px-3 py-1 rounded">Buy</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
