// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './index.css';

/* --------------------- Login Page --------------------- */
function LoginPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Hardcoded credentials:
    // admin: username "admin", password "password"
    // user1: username "user1", password "password"
    // user2: username "user2", password "password"
    if (
      (username === 'admin' && password === 'password') ||
      (username === 'user1' && password === 'password') ||
      (username === 'user2' && password === 'password')
    ) {
      localStorage.setItem('user', username);
      localStorage.setItem('role', username === 'admin' ? 'admin' : username);
      navigate('/dashboard');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="min-h-screen bg-black text-white font-mono flex flex-col justify-center items-center">
      <h1 className="text-4xl mb-8">Library Login</h1>
      <form onSubmit={handleLogin} className="flex flex-col gap-4 w-80">
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          className="p-3 rounded text-black" 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          className="p-3 rounded text-black" 
        />
        <button type="submit" className="py-3 px-6 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80">
          Login
        </button>
      </form>
    </div>
  );
}

/* --------------------- Dashboard --------------------- */
function Dashboard() {
  const navigate = useNavigate();
  const user = localStorage.getItem('user');
  const role = localStorage.getItem('role');
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '', id: null });

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/books');
      setBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const resetForm = () => {
    setForm({ title: '', author: '', genre: '', id: null });
  };

  // Admin: add new book or update an existing book
  const handleAddOrUpdate = async () => {
    if (!form.title || !form.author || !form.genre) return;
    try {
      if (form.id) {
        await axios.put(`http://localhost:5000/books/${form.id}`, {
          title: form.title,
          author: form.author,
          genre: form.genre,
        });
      } else {
        await axios.post('http://localhost:5000/books', {
          title: form.title,
          author: form.author,
          genre: form.genre,
        });
      }
      resetForm();
      fetchBooks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (book) => {
    setForm({ ...book });
  };

  // User operations: Borrow, Return, Buy (user action: using the username)
  const handleBorrow = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/books/${id}/borrow`, { username: user });
      fetchBooks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleReturn = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/books/${id}/return`, { username: user });
      fetchBooks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleBuy = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/books/${id}/buy`);
      fetchBooks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-black text-white font-mono p-6">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold">Library Dashboard</h1>
        <button onClick={handleLogout} className="py-3 px-6 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80">
          Logout
        </button>
      </div>

      {role === 'admin' && (
        <div className="mb-8 p-6 bg-gray-800 rounded-lg shadow-lg grid grid-cols-1 md:grid-cols-3 gap-4">
          <input 
            type="text" 
            placeholder="Title" 
            value={form.title} 
            onChange={e => setForm({ ...form, title: e.target.value })} 
            className="p-3 rounded text-black" 
          />
          <input 
            type="text" 
            placeholder="Author" 
            value={form.author} 
            onChange={e => setForm({ ...form, author: e.target.value })} 
            className="p-3 rounded text-black" 
          />
          <input 
            type="text" 
            placeholder="Genre" 
            value={form.genre} 
            onChange={e => setForm({ ...form, genre: e.target.value })} 
            className="p-3 rounded text-black" 
          />
          <button 
            onClick={handleAddOrUpdate} 
            className="col-span-1 md:col-span-3 py-3 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80"
          >
            {form.id ? 'Update Book' : 'Add Book'}
          </button>
        </div>
      )}

      <div className="space-y-6">
        {books.map(book => (
          <div key={book.id} className="bg-gray-800 p-6 rounded-lg shadow-lg flex flex-col md:flex-row justify-between items-center gap-4">
            <div className="text-center md:text-left">
              <p className="text-2xl font-bold">{book.title}</p>
              <p className="text-sm">by {book.author} | {book.genre}</p>
              <p className="text-sm">Status: {book.status}{book.borrowedBy ? `, Borrowed by ${book.borrowedBy}` : ''}</p>
            </div>
            <div className="flex gap-4 flex-wrap justify-center">
              {role === 'admin' && (
                <>
                  <button onClick={() => handleEdit(book)} className="py-2 px-4 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80">Edit</button>
                  <button onClick={() => handleDelete(book.id)} className="py-2 px-4 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80">Delete</button>
                </>
              )}
              {role !== 'admin' && (
                <>
                  <button onClick={() => handleBorrow(book.id)} className="py-2 px-4 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80" disabled={book.status !== 'available'}>Borrow</button>
                  <button onClick={() => handleReturn(book.id)} className="py-2 px-4 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80" disabled={book.status !== 'borrowed'}>Return</button>
                  <button onClick={() => handleBuy(book.id)} className="py-2 px-4 bg-[#0818A8] rounded-full hover:bg-[#0818A8]/80" disabled={book.status !== 'available'}>Buy</button>
                </>
              )}
            </div>
          </div>
        ))}\n      </div>
    </div>
  );
}

/* --------------------- Routing Wrapper --------------------- */
function AppWrapper() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/*" element={<LoginPage />} />
      </Routes>
    </Router>
  );
}

export default AppWrapper;
