// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import { Button } from '@radix-ui/themes';
import './index.css';

function LoginPage({ setRole }) {
  const navigate = useNavigate();

  const handleLogin = (role) => {
    setRole(role);
    navigate('/home');
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-br from-indigo-500 to-purple-500 text-white">
      <h2 className="text-4xl font-bold mb-8">Library Management</h2>
      <div className="flex gap-6">
        <Button onClick={() => handleLogin('admin')} className="bg-white text-indigo-600 hover:bg-indigo-100">Admin</Button>
        <Button onClick={() => handleLogin('user')} className="bg-white text-purple-600 hover:bg-purple-100">User</Button>
      </div>
    </div>
  );
}

function Home({ role }) {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', genre: '' });
  const [editBookId, setEditBookId] = useState(null);
  const navigate = useNavigate();

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  const handleLogout = () => {
    navigate('/');
  };

  const addBook = async () => {
    await axios.post('http://localhost:5000/books', newBook);
    setNewBook({ title: '', author: '', genre: '' });
    fetchBooks();
  };

  const updateBook = async (id) => {
    await axios.put(`http://localhost:5000/books/${id}`, newBook);
    setEditBookId(null);
    setNewBook({ title: '', author: '', genre: '' });
    fetchBooks();
  };

  const deleteBook = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  const borrowBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/borrow`);
    fetchBooks();
  };

  const buyBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/buy`);
    fetchBooks();
  };

  const returnBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/return`);
    fetchBooks();
  };

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-3xl font-bold text-gray-800">Welcome, {role}</h2>
        <Button className="bg-red-500 text-white hover:bg-red-600" onClick={handleLogout}>Log Out</Button>
      </div>

      {role === 'admin' && (
        <div className="mb-8 p-6 bg-white shadow-lg rounded-xl">
          <h3 className="text-xl font-semibold mb-4 text-gray-700">{editBookId ? 'Edit Book' : 'Add Book'}</h3>
          <div className="grid grid-cols-3 gap-4">
            <input className="border p-2 rounded" placeholder="Title" value={newBook.title} onChange={e => setNewBook({ ...newBook, title: e.target.value })} />
            <input className="border p-2 rounded" placeholder="Author" value={newBook.author} onChange={e => setNewBook({ ...newBook, author: e.target.value })} />
            <input className="border p-2 rounded" placeholder="Genre" value={newBook.genre} onChange={e => setNewBook({ ...newBook, genre: e.target.value })} />
          </div>
          <Button className="mt-4 bg-indigo-600 text-white hover:bg-indigo-700" onClick={editBookId ? () => updateBook(editBookId) : addBook}>
            {editBookId ? 'Update Book' : 'Add Book'}
          </Button>
        </div>
      )}

      <div className="bg-white shadow rounded-xl p-6">
        <h3 className="text-xl font-semibold mb-4 text-gray-700">Available Books</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto border">
            <thead>
              <tr className="bg-indigo-100 text-indigo-800">
                <th className="px-4 py-2 border">Title</th>
                <th className="px-4 py-2 border">Author</th>
                <th className="px-4 py-2 border">Genre</th>
                <th className="px-4 py-2 border">Status</th>
                <th className="px-4 py-2 border">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map(book => (
                <tr key={book.id} className="text-center">
                  <td className="border px-4 py-2">{book.title}</td>
                  <td className="border px-4 py-2">{book.author}</td>
                  <td className="border px-4 py-2">{book.genre}</td>
                  <td className="border px-4 py-2">{book.status}</td>
                  <td className="border px-4 py-2 space-x-2">
                    {role === 'admin' && (
                      <>
                        <Button className="bg-blue-500 text-white" onClick={() => { setNewBook(book); setEditBookId(book.id); }}>Edit</Button>
                        <Button className="bg-red-400 text-white" onClick={() => deleteBook(book.id)}>Delete</Button>
                      </>
                    )}
                    {role === 'user' && (
                      <>
                        <Button className="bg-yellow-400 text-white" disabled={book.status !== 'available'} onClick={() => borrowBook(book.id)}>Borrow</Button>
                        <Button className="bg-green-500 text-white" onClick={() => buyBook(book.id)}>Buy</Button>
                        <Button className="bg-gray-600 text-white" disabled={book.status !== 'borrowed'} onClick={() => returnBook(book.id)}>Return</Button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [role, setRole] = useState(null);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setRole={setRole} />} />
        <Route path="/home" element={<Home role={role} />} />
      </Routes>
    </Router>
  );
}

export default App;
