// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [books, setBooks] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [userRole, setUserRole] = useState('user'); // 'admin' or 'user'
    const [newBook, setNewBook] = useState({ title: '', author: '', genre: '' });

    useEffect(() => {
        fetchBooks();
    }, []);

    const fetchBooks = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/books');
            setBooks(res.data);
        } catch (err) {
            console.error('Error fetching books:', err);
        }
    };

    const handleBorrow = async (id) => {
        alert(`Book with ID ${id} borrowed!`);
        // Implement actual borrowing logic with backend if needed
    };

    const handlePurchase = async (id) => {
        alert(`Book with ID ${id} purchased!`);
        // Implement actual purchase logic with backend if needed
    };

    const handleEdit = async (id) => {
        const updatedTitle = prompt('Enter new title:');
        if (!updatedTitle) return;
        try {
            await axios.put(`http://localhost:5000/api/books/${id}`, { title: updatedTitle });
            fetchBooks();
        } catch (err) {
            console.error('Error editing book:', err);
        }
    };

    const handleAddBook = async () => {
        try {
            await axios.post('http://localhost:5000/api/books', newBook);
            setNewBook({ title: '', author: '', genre: '' });
            fetchBooks();
        } catch (err) {
            console.error('Error adding book:', err);
        }
    };

    const filteredBooks = books.filter(
        (book) =>
            book.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            book.author.toLowerCase().includes(searchQuery.toLowerCase()) ||
            book.genre.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="App">
            <header>
                <h1>Library Management System</h1>
                <div>
                    <label>Login as: </label>
                    <select onChange={(e) => setUserRole(e.target.value)} value={userRole}>
                        <option value="user">User</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
            </header>

            <input
                type="text"
                placeholder="Search books..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            {userRole === 'admin' && (
                <div className="add-book-form">
                    <h3>Add New Book</h3>
                    <input
                        type="text"
                        placeholder="Title"
                        value={newBook.title}
                        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Author"
                        value={newBook.author}
                        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Genre"
                        value={newBook.genre}
                        onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
                    />
                    <button onClick={handleAddBook}>Add Book</button>
                </div>
            )}

            <div className="book-list">
                {filteredBooks.map((book) => (
                    <div key={book.id} className="book-card">
                        <h3>{book.title}</h3>
                        <p><strong>Author:</strong> {book.author}</p>
                        <p><strong>Genre:</strong> {book.genre}</p>

                        {userRole === 'user' && (
                            <>
                                <button onClick={() => handleBorrow(book.id)}>Borrow</button>
                                <button onClick={() => handlePurchase(book.id)}>Purchase</button>
                            </>
                        )}

                        {userRole === 'admin' && (
                            <button onClick={() => handleEdit(book.id)}>Edit</button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
