// frontend/src/components/AddBook.js
import React, { useState } from 'react';
import axios from 'axios';

function AddBook({ onBookAdded }) {
    const [book, setBook] = useState({
        title: '',
        author: '',
        price: '',
        quantity: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBook(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:3001/api/books', book);
            if (res.data.success) {
                alert('Book added!');
                setBook({ title: '', author: '', price: '', quantity: '' });
                onBookAdded(); // Optional callback to refresh book list
            } else {
                alert('Failed to add book');
            }
        } catch (err) {
            console.error('Error adding book:', err);
            alert('Error adding book. Check backend logs.');
        }
    };

    return (
        <div>
            <h2>Add a Book</h2>
            <form onSubmit={handleSubmit}>
                <input name="title" value={book.title} onChange={handleChange} placeholder="Title" required />
                <input name="author" value={book.author} onChange={handleChange} placeholder="Author" required />
                <input name="price" value={book.price} onChange={handleChange} placeholder="Price" type="number" required />
                <input name="quantity" value={book.quantity} onChange={handleChange} placeholder="Quantity" type="number" required />
                <button type="submit">Add Book</button>
            </form>
        </div>
    );
}

export default AddBook;
