// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const initialBooks = [
  { id: 1, title: '1984', author: 'George Orwell', genre: 'Dystopian', status: 'available' },
  { id: 2, title: 'To Kill a Mockingbird', author: 'Harper Lee', genre: 'Classic', status: 'available' },
  { id: 3, title: 'The Great Gatsby', author: 'F. Scott Fitzgerald', genre: 'Classic', status: 'available' },
];

function Login() {
  const navigate = useNavigate();
  const [role, setRole] = useState('user');

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate(`/home/${role}`);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Library Management Login</h2>
      <form onSubmit={handleSubmit} className="card p-4 shadow">
        <div className="form-group">
          <label>Select Role:</label>
          <select className="form-control" value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary mt-3 w-100">Enter</button>
      </form>
    </div>
  );
}

function Home() {
  const { role } = useParams();
  const [books, setBooks] = useState([]);
  const [editingBook, setEditingBook] = useState(null);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });

  useEffect(() => {
    const storedBooks = localStorage.getItem('books');
    if (storedBooks) {
      setBooks(JSON.parse(storedBooks));
    } else {
      localStorage.setItem('books', JSON.stringify(initialBooks));
      setBooks(initialBooks);
    }
  }, []);

  const updateBooks = (updated) => {
    setBooks(updated);
    localStorage.setItem('books', JSON.stringify(updated));
  };

  const handleBorrow = (id) => {
    const updated = books.map((book) =>
      book.id === id ? { ...book, status: 'borrowed' } : book
    );
    updateBooks(updated);
  };

  const handleBuy = (id) => {
    const updated = books.filter((book) => book.id !== id);
    updateBooks(updated);
  };

  const handleDelete = (id) => {
    const updated = books.filter((book) => book.id !== id);
    updateBooks(updated);
  };

  const handleEdit = (book) => {
    setEditingBook(book);
    setForm({ title: book.title, author: book.author, genre: book.genre });
  };

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (editingBook) {
      const updated = books.map((book) =>
        book.id === editingBook.id ? { ...book, ...form } : book
      );
      updateBooks(updated);
      setEditingBook(null);
    } else {
      const newBook = {
        id: Date.now(),
        ...form,
        status: 'available',
      };
      updateBooks([...books, newBook]);
    }
    setForm({ title: '', author: '', genre: '' });
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Welcome {role === 'admin' ? 'Admin' : 'User'}!</h2>

      {role === 'admin' && (
        <div className="card p-3 mb-4 shadow">
          <h4>{editingBook ? 'Edit Book' : 'Add New Book'}</h4>
          <form onSubmit={handleFormSubmit} className="row g-3">
            <div className="col-md-4">
              <input
                type="text"
                name="title"
                className="form-control"
                placeholder="Title"
                value={form.title}
                onChange={handleFormChange}
                required
              />
            </div>
            <div className="col-md-4">
              <input
                type="text"
                name="author"
                className="form-control"
                placeholder="Author"
                value={form.author}
                onChange={handleFormChange}
                required
              />
            </div>
            <div className="col-md-4">
              <input
                type="text"
                name="genre"
                className="form-control"
                placeholder="Genre"
                value={form.genre}
                onChange={handleFormChange}
                required
              />
            </div>
            <div className="col-12">
              <button className="btn btn-success w-100" type="submit">
                {editingBook ? 'Update Book' : 'Add Book'}
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="row">
        {books.map((book) => (
          <div className="col-md-4 mb-4" key={book.id}>
            <div className="card h-100 shadow">
              <div className="card-body">
                <h5 className="card-title">{book.title}</h5>
                <p className="card-text"><strong>Author:</strong> {book.author}</p>
                <p className="card-text"><strong>Genre:</strong> {book.genre}</p>
                <p className="card-text"><strong>Status:</strong> {book.status}</p>
              </div>
              <div className="card-footer d-flex flex-column gap-2">
                {role === 'user' && (
                  <>
                    <button
                      className="btn btn-success"
                      onClick={() => handleBorrow(book.id)}
                      disabled={book.status !== 'available'}
                    >
                      Borrow
                    </button>
                    <button className="btn btn-primary" onClick={() => handleBuy(book.id)}>Buy</button>
                  </>
                )}
                {role === 'admin' && (
                  <>
                    <button className="btn btn-warning" onClick={() => handleEdit(book)}>Edit</button>
                    <button className="btn btn-danger" onClick={() => handleDelete(book.id)}>Delete</button>
                  </>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/home/:role" element={<Home />} />
      </Routes>
    </Router>
  );
}

export default App;
