// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [books, setBooks] = useState([]);
  const [role, setRole] = useState(null);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });
  const [editingId, setEditingId] = useState(null);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  const handleAddOrEdit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await axios.put(`http://localhost:5000/books/${editingId}`, form);
      setEditingId(null);
    } else {
      await axios.post('http://localhost:5000/books', form);
    }
    setForm({ title: '', author: '', genre: '' });
    fetchBooks();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  const handleBorrow = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/borrow`);
    fetchBooks();
  };

  const handleReturn = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/return`);
    fetchBooks();
  };

  const handleBuy = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/buy`);
    fetchBooks();
  };

  const handleEdit = (book) => {
    setForm({ title: book.title, author: book.author, genre: book.genre });
    setEditingId(book.id);
  };

  const handleLogout = () => {
    setRole(null);
  };

  if (!role) {
    return (
      <div className="container mt-5">
        <h2 className="text-center">Login</h2>
        <div className="d-flex justify-content-center">
          <button className="btn btn-primary m-2" onClick={() => setRole('user')}>Login as User</button>
          <button className="btn btn-secondary m-2" onClick={() => setRole('admin')}>Login as Admin</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{role === 'admin' ? 'Admin Panel' : 'User Home'} </h2>
        <button className="btn btn-outline-danger" onClick={handleLogout}>Logout</button>
      </div>

      {role === 'admin' && (
        <form onSubmit={handleAddOrEdit} className="mb-4">
          <div className="row g-2">
            <div className="col"><input className="form-control" placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} required /></div>
            <div className="col"><input className="form-control" placeholder="Author" value={form.author} onChange={e => setForm({ ...form, author: e.target.value })} required /></div>
            <div className="col"><input className="form-control" placeholder="Genre" value={form.genre} onChange={e => setForm({ ...form, genre: e.target.value })} required /></div>
            <div className="col"><button type="submit" className="btn btn-success w-100">{editingId ? 'Update' : 'Add'} Book</button></div>
          </div>
        </form>
      )}

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.genre}</td>
              <td>{book.status}</td>
              <td>
                {role === 'admin' ? (
                  <>
                    <button className="btn btn-sm btn-warning me-2" onClick={() => handleEdit(book)}>Edit</button>
                    <button className="btn btn-sm btn-danger" onClick={() => handleDelete(book.id)}>Delete</button>
                  </>
                ) : (
                  <>
                    {book.status === 'available' && (
                      <>
                        <button className="btn btn-sm btn-primary me-2" onClick={() => handleBorrow(book.id)}>Borrow</button>
                        <button className="btn btn-sm btn-success me-2" onClick={() => handleBuy(book.id)}>Buy</button>
                      </>
                    )}
                    {book.status === 'borrowed' && (
                      <button className="btn btn-sm btn-info" onClick={() => handleReturn(book.id)}>Return</button>
                    )}
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
