import React, { useState, useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import {
  Edit,
  Trash2,
  Plus,
  LogOut,
  BookOpen,
  ShoppingCart,
  ArrowLeft,
  Search,
  User,
  KeyRound
} from 'lucide-react';
import { cn } from '@/lib/utils';
import { motion, AnimatePresence } from 'framer-motion';

// Mock API functions (replace with actual API calls in a real application)
const api = {
  login: async (credentials: any, role: 'user' | 'admin') => {
    // Simulate login
    await new Promise((resolve) => setTimeout(resolve, 500)); // Simulate network delay

    if (role === 'admin') {
      if (credentials.username === 'admin' && credentials.password === '123456') {
        return { success: true, role: 'admin' };
      } else {
        return { success: false, message: 'Invalid admin credentials' };
      }
    } else { // role === 'user'
      if (
        (credentials.username === 'aarush' ||
          credentials.username === 'ishita' ||
          credentials.username === 'havish') &&
        credentials.password === '123456'
      ) {
        return { success: true, role: 'user', username: credentials.username };
      } else {
        return { success: false, message: 'Invalid user credentials' };
      }
    }
  },
  getBooks: async () => {
    // Simulate fetching books from the database
    await new Promise((resolve) => setTimeout(resolve, 500));
    return [
      { id: '1', title: 'The Lord of the Rings', author: 'J.R.R. Tolkien', cover: 'https://m.media-amazon.com/images/I/71jLBXtWJWL._AC_UF1000,1000_QL80_.jpg', genre: 'Fantasy', borrowedBy: null },
      { id: '2', title: 'Pride and Prejudice', author: 'Jane Austen', cover: 'https://m.media-amazon.com/images/I/71Q1tPupKjL._AC_UF894,1000,1000_QL80_.jpg', genre: 'Romance', borrowedBy: null },
      { id: '3', title: 'The Hitchhiker\'s Guide to the Galaxy', author: 'Douglas Adams', cover: 'https://m.media-amazon.com/images/I/51+qSD6UwfL._AC_UF1000,1000_QL80_.jpg', genre: 'Science Fiction', borrowedBy: 'user123' },
      { id: '4', title: 'To Kill a Mockingbird', author: 'Harper Lee', cover: 'https://m.media-amazon.com/images/I/71yNgTMEcpL._AC_UF1000,1000_QL80_.jpg', genre: 'Classic', borrowedBy: null },
