// Components
const LoginPage = ({ onLogin }: { onLogin: (role: 'user' | 'admin', credentials?: any) => void }) => {
  const [isUserLogin, setIsUserLogin] = useState < boolean | null > (null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLoginClick = async () => {
    if (isUserLogin === null) return; // Prevent login attempt without selecting user/admin

    if (!username || !password) {
      setError('Please enter both username and password.');
      return;
    }

    setError(''); // Clear any previous error
    const credentials = { username, password };
    const role = isUserLogin ? 'user' : 'admin';
    const result = await api.login(credentials, role);
    if (result.success) {
      onLogin(role, credentials);
    } else {
      setError(result.message);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-black">
      <h1 className="text-4xl font-bold text-white mb-8">Library Management System</h1>
      {isUserLogin === null ? (
        <div className="space-x-4">
          <Button
            onClick={() => setIsUserLogin(true)}
            className="bg-gray-800 hover:bg-gray-700 text-white font-semibold px-6 py-3 rounded-md"
          >
            Login as User
          </Button>
          <Button
            onClick={() => setIsUserLogin(false)}
            className="bg-gray-800 hover:bg-gray-700 text-white font-semibold px-6 py-3 rounded-md"
          >
            Login as Admin
          </Button>
        </div>
      ) : (
        <div className="w-full max-w-md bg-gray-800 p-6 rounded-lg shadow-lg">
          <h2 className="text-2xl font-semibold text-white mb-4">
            {isUserLogin ? 'User Login' : 'Admin Login'}
          </h2>
          <Input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="mb-4 bg-gray-700 border-gray-600 text-white"
            prefix={<User className="mr-2 h-4 w-4 text-gray-400" />}
          />
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mb-6 bg-gray-700 border-gray-600 text-white"
            prefix={<KeyRound className="mr-2 h-4 w-4 text-gray-400" />}
          />
          {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
          <Button
            onClick={handleLoginClick}
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 rounded-md"
          >
            Login
          </Button>
          <Button
            onClick={() => setIsUserLogin(null)}
            className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white font-semibold py-3 rounded-md"
          >
            Back
          </Button>
        </div>
      )}
    </div>
  );
};

