const LibraryApp = () => {
  const [userRole, setUserRole] = useState < 'user' | 'admin' | null > (null);
  const [username, setUsername] = useState < string | null > (null); // Store logged-in username

  const [loading, setLoading] = useState(true);
  const [addBookDialogOpen, setAddBookDialogOpen] = useState(false);
  const [editBookDialogOpen, setEditBookDialogOpen] = useState(false);
  const [selectedBook, setSelectedBook] = useState < Book | null > (null);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch books on component mount and when a book is added/updated/deleted
  const fetchBooks = useCallback(async () => {
    setLoading(true);
    try {
      const fetchedBooks = await api.getBooks();
      setBooks(fetchedBooks);
    } catch (error) {
      console.error('Failed to fetch books:', error);
      // Handle error (e.g., show error message to user)
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchBooks();
  }, [fetchBooks]);

  const handleLogin = async (role: 'user' | 'admin', credentials?: any) => {
    try {
      const result = await api.login(credentials, role);
      if (result.success) {
        setUserRole(role);
        setUsername(result.username || 'Admin'); // Set username, default to "Admin"
      } else {
        setMessage(result.message);
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Login failed:', error);
      setMessage('An error occurred during login.');
      setTimeout(() => setMessage(''), 3000);
      // Handle error
    }
  };

  const handleLogout = () => {
    setUserRole(null);
    setUsername(null);
  };

  const handleBorrowBook = async (bookId: string) => {
    // In a real app, you'd get the user ID from the authentication context
    const userId = 'user123'; // Mock user ID
    try {
      const result = await api.borrowBook(bookId, userId);
      if (result.success) {
        setMessage(result.message);
        // Update the book's borrowedBy property
        setBooks(prevBooks =>
          prevBooks.map(book =>
            book.id === bookId ? { ...book, borrowedBy: userId } : book
          )
        );
        setTimeout(() => setMessage(''), 3000); // Clear message
      } else {
        setMessage('Failed to borrow book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error borrowing book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handlePurchaseBook = async (bookId: string) => {
    try {
      const result = await api.purchaseBook(bookId);
      if (result.success) {
        setMessage(result.message);
        // Remove the book from the list
        setBooks(prevBooks => prevBooks.filter(book => book.id !== bookId));
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to purchase book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error purchasing book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handleReturnBook = async (bookId: string) => {
    try {
      const result = await api.returnBook(bookId);
      if (result.success) {
        setMessage(result.message);
        // Update the book's borrowedBy property to null
        setBooks(prevBooks =>
          prevBooks.map(book =>
            book.id === bookId ? { ...book, borrowedBy: null } : book
          )
        );
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to return book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error returning book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handleAddBook = async (newBook: Omit<Book, 'id' | 'borrowedBy'>) => {
    try {
      const result = await api.addBook(newBook);
      if (result.success) {
        setBooks(prevBooks => [...prevBooks, result.book]);
        setMessage('Book added successfully!');
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to add book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error adding book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handleUpdateBook = async (bookId: string, updates: Partial<Omit<Book, 'id' | 'borrowedBy'>>) => {
    try {
      const result = await api.updateBook(bookId, updates);
      if (result.success) {
        setMessage(result.message);
        setBooks(prevBooks =>
          prevBooks.map(book =>
            book.id === bookId ? { ...book, ...result.updates } : book
          )
        );
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to update book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error updating book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handleDeleteBook = async (bookId: string) => {
    try {
      const result = await api.deleteBook(bookId);
      if (result.success) {
        setMessage(result.message);
        setBooks(prevBooks => prevBooks.filter(book => book.id !== bookId));
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to delete book.');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (error) {
      console.error('Error deleting book:', error);
      setMessage('An error occurred.');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const filteredBooks = books.filter(book =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.genre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="bg-black min-h-screen font-roboto">
      {message && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          className="fixed top-4 left-1/2 -translate-x-1/2 bg-gray-700 text-white px-6 py-2 rounded-md shadow-lg z-50"
        >
          {message}
        </motion.div>
      )}
      {!userRole ? (
        <LoginPage onLogin={handleLogin} />
      ) : (
        <div className="container mx-auto p-4">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold text-white">
              {userRole === 'user'
                ? `Welcome, ${username}`
                : 'Admin Dashboard'}
            </h1>
            <Button
              onClick={handleLogout}
              className="bg-gray-800 hover:bg-gray-700 text-white font-semibold px-4 py-2 rounded-md"
            >
              <LogOut className="mr-2 h-4 w-4" /> Logout
            </Button>
          </div>

          {userRole === 'user' ? (
            <>
              <div className="mb-6 flex justify-center">
                <div className="relative w-full max-w-md">
                  <Input
                    type="text"
                    placeholder="Search for books..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pr-10 bg-gray-700 border-gray-600 text-white"
                  />
                  <Search className="absolute right-3 top-3 h-5 w-5 text-gray-400" />
                </div>
              </div>
              {loading ? (
                <div className="text-white">Loading books...</div> // Simple loading indicator
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                  <AnimatePresence>
                    {filteredBooks.map((book) => (
                      <motion.div
                        key={book.id}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -20 }}
                      >
                        <BookCard
                          book={book}
                          onBorrow={handleBorrowBook}
                          onPurchase={handlePurchaseBook}
                          onReturn={handleReturnBook}
                        />
                      </motion.div>
                    ))}
                  </AnimatePresence>
                </div>
              )}
            </>
          ) : (
            <>
              <div className="flex justify-end mb-4">
                <Button
                  onClick={() => setAddBookDialogOpen(true)}
                  className="bg-gray-800 hover:bg-gray-700 text-white font-semibold px-4 py-2 rounded-md"
                >
                  <Plus className="mr-2 h-4 w-4" /> Add Book
                </Button>
              </div>
              {loading ? (
                <div className="text-white">Loading books...</div> // Simple loading indicator
              ) : (
                <AdminBookList
                  books={books}
                  onEdit={(bookId) => {
                    const bookToEdit = books.find((b) => b.id === bookId);
                    if (bookToEdit) {
                      setSelectedBook(bookToEdit);
                      setEditBookDialogOpen(true);
                    }
                  }}
                  onDelete={handleDeleteBook}
                />
              )}
              <AddBookDialog
                onAdd={handleAddBook}
                isOpen={addBookDialogOpen}
                onClose={() => setAddBookDialogOpen(false)}
              />
              <EditBookDialog
                book={selectedBook}
                onUpdate={handleUpdateBook}
                isOpen={editBookDialogOpen}
                onClose={() => {
                  setEditBookDialogOpen(false);
                  setSelectedBook(null);
                }}
              />
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default LibraryApp;
