import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useParams } from "react-router-dom";

const sampleBooks = [
  { id: 1, title: "Book One", author: "Author A" },
  { id: 2, title: "Book Two", author: "Author B" },
];

function HomePage({ userRole, books, setBooks }) {
  const navigate = useNavigate();

  const borrowBook = (id) => {
    alert(`Borrowed book with id ${id}`);
  };

  const purchaseBook = (id) => {
    alert(`Purchased book with id ${id}`);
  };

  const returnBook = (id) => {
    alert(`Returned book with id ${id}`);
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <h1 className="text-2xl font-bold col-span-full">Welcome, {userRole}!</h1>
      {userRole === "admin" && (
        <div className="col-span-full space-y-4">
          <button onClick={() => navigate("/add-book")} className="px-4 py-2 bg-blue-600 text-white">Add Book</button>
          {books.map((book) => (
            <div key={book.id} className="p-4 border rounded shadow-md">
              <div className="font-semibold">{book.title}</div>
              <div className="text-sm text-gray-600">by {book.author}</div>
              <div className="mt-2 space-x-2">
                <button onClick={() => navigate(`/edit-book/${book.id}`)} className="px-2 py-1 bg-yellow-400 text-white">Edit</button>
                <button onClick={() => setBooks(books.filter(b => b.id !== book.id))} className="px-2 py-1 bg-red-500 text-white">Delete</button>
              </div>
            </div>
          ))}
        </div>
      )}

      {userRole === "user" && books.map((book) => (
        <div key={book.id} className="p-4 border rounded shadow-md">
          <div className="font-semibold">{book.title}</div>
          <div className="text-sm text-gray-600">by {book.author}</div>
          <div className="mt-2 space-x-2">
            <button onClick={() => borrowBook(book.id)} className="px-2 py-1 bg-blue-500 text-white">Borrow</button>
            <button onClick={() => purchaseBook(book.id)} className="px-2 py-1 bg-green-500 text-white">Purchase</button>
            <button onClick={() => returnBook(book.id)} className="px-2 py-1 bg-yellow-500 text-white">Return</button>
          </div>
        </div>
      ))}
    </div>
  );
}

function AddBookPage({ books, setBooks }) {
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");

  const addBook = () => {
    const newBook = { id: Date.now(), title, author };
    setBooks([...books, newBook]);
    navigate("/");
  };

  return (
    <div className="space-y-2">
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" className="border p-2 block w-full" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} placeholder="Author" className="border p-2 block w-full" />
      <button onClick={addBook} className="px-4 py-2 bg-blue-600 text-white">Add</button>
    </div>
  );
}

function EditBookPage({ books, setBooks }) {
  const { id } = useParams();
  const book = books.find((b) => b.id === parseInt(id));
  const [title, setTitle] = useState(book?.title || "");
  const [author, setAuthor] = useState(book?.author || "");
  const navigate = useNavigate();

  const updateBook = () => {
    setBooks(books.map((b) => (b.id === parseInt(id) ? { ...b, title, author } : b)));
    navigate("/");
  };

  return (
    <div className="space-y-2">
      <input value={title} onChange={(e) => setTitle(e.target.value)} className="border p-2 block w-full" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} className="border p-2 block w-full" />
      <button onClick={updateBook} className="px-4 py-2 bg-yellow-600 text-white">Update</button>
    </div>
  );
}

function LoginPage({ setUserRole }) {
  const navigate = useNavigate();

  const loginAs = (role) => {
    setUserRole(role);
    navigate("/");
  };

  return (
    <div className="p-6 space-y-4 text-center">
      <h2 className="text-2xl font-bold">Login</h2>
      <button onClick={() => loginAs("admin")} className="px-4 py-2 bg-blue-600 text-white">Login as Admin</button>
      <button onClick={() => loginAs("user")} className="px-4 py-2 bg-green-600 text-white">Login as User</button>
    </div>
  );
}

function LogoutButton({ setUserRole }) {
  const navigate = useNavigate();
  return (
    <button
      onClick={() => {
        setUserRole(null);
        navigate("/login");
      }}
      className="px-4 py-2 bg-red-600 text-white mt-4"
    >
      Logout
    </button>
  );
}

export default function App() {
  const [userRole, setUserRole] = useState(null);
  const [books, setBooks] = useState(sampleBooks);

  return (
    <Router>
      {!userRole ? (
        <Routes>
          <Route path="*" element={<LoginPage setUserRole={setUserRole} />} />
        </Routes>
      ) : (
        <div className="p-6">
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <HomePage userRole={userRole} books={books} setBooks={setBooks} />
                  <LogoutButton setUserRole={setUserRole} />
                </>
              }
            />
            {userRole === "admin" && (
              <>
                <Route path="/add-book" element={<AddBookPage books={books} setBooks={setBooks} />} />
                <Route path="/edit-book/:id" element={<EditBookPage books={books} setBooks={setBooks} />} />
              </>
            )}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      )}
    </Router>
  );
}
