import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
  useParams,
} from "react-router-dom";
import { motion } from "framer-motion";
import { Book, PlusCircle, Home, LogOut } from "lucide-react";

const sampleBooks = [
  { id: 1, title: "The Odyssey", author: "Homer" },
  { id: 2, title: "Hamlet", author: "William Shakespeare" },
];

function Layout({ children }) {
  return (
    <div className="flex h-screen bg-gradient-to-br from-blue-100 to-white">
      <aside className="w-64 bg-white/40 backdrop-blur-md shadow-xl p-6 text-gray-800 border-r border-gray-200">
        <div className="text-3xl font-black text-blue-800 tracking-tight mb-6">📚 MyLibrary</div>
        <nav className="space-y-4 text-lg">
          <a href="/" className="flex items-center gap-2 hover:text-blue-600">
            <Home size={20} /> Home
          </a>
          <a href="/add-book" className="flex items-center gap-2 hover:text-blue-600">
            <PlusCircle size={20} /> Add Book
          </a>
        </nav>
      </aside>
      <main className="flex-1 p-10 overflow-y-auto bg-white/60 rounded-l-3xl shadow-inner">
        {children}
      </main>
    </div>
  );
}

function HomePage({ userRole, books, setBooks }) {
  const navigate = useNavigate();

  const borrowBook = (id) => alert(`Borrowed book ID ${id}`);
  const purchaseBook = (id) => alert(`Purchased book ID ${id}`);
  const returnBook = (id) => alert(`Returned book ID ${id}`);

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="space-y-6">
      <h2 className="text-3xl font-bold text-blue-900">Welcome, {userRole}</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {books.map((book) => (
          <motion.div
            key={book.id}
            whileHover={{ scale: 1.02 }}
            className="bg-white shadow-lg rounded-xl p-6 border border-blue-100"
          >
            <h3 className="text-xl font-semibold text-gray-900">{book.title}</h3>
            <p className="text-gray-500 italic mb-4">by {book.author}</p>
            <div className="flex gap-2 flex-wrap">
              {userRole === "admin" ? (
                <>
                  <button onClick={() => navigate(`/edit-book/${book.id}`)} className="bg-yellow-400 px-3 py-1 rounded text-white hover:bg-yellow-500">Edit</button>
                  <button onClick={() => setBooks(books.filter((b) => b.id !== book.id))} className="bg-red-500 px-3 py-1 rounded text-white hover:bg-red-600">Delete</button>
                </>
              ) : (
                <>
                  <button onClick={() => borrowBook(book.id)} className="bg-blue-500 px-3 py-1 rounded text-white hover:bg-blue-600">Borrow</button>
                  <button onClick={() => purchaseBook(book.id)} className="bg-green-500 px-3 py-1 rounded text-white hover:bg-green-600">Purchase</button>
                  <button onClick={() => returnBook(book.id)} className="bg-yellow-500 px-3 py-1 rounded text-white hover:bg-yellow-600">Return</button>
                </>
              )}
            </div>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
}

function AddBookPage({ books, setBooks }) {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const navigate = useNavigate();

  const addBook = () => {
    const newBook = { id: Date.now(), title, author };
    setBooks([...books, newBook]);
    navigate("/");
  };

  return (
    <div className="max-w-md space-y-4">
      <h2 className="text-2xl font-bold text-blue-800">Add a Book</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" className="w-full p-3 border border-gray-300 rounded-lg" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} placeholder="Author" className="w-full p-3 border border-gray-300 rounded-lg" />
      <button onClick={addBook} className="bg-blue-700 text-white px-5 py-2 rounded-lg shadow hover:bg-blue-800">Add Book</button>
    </div>
  );
}

function EditBookPage({ books, setBooks }) {
  const { id } = useParams();
  const book = books.find((b) => b.id === parseInt(id));
  const [title, setTitle] = useState(book?.title || "");
  const [author, setAuthor] = useState(book?.author || "");
  const navigate = useNavigate();

  const updateBook = () => {
    setBooks(books.map((b) => (b.id === book.id ? { ...b, title, author } : b)));
    navigate("/");
  };

  return (
    <div className="max-w-md space-y-4">
      <h2 className="text-2xl font-bold text-yellow-700">Edit Book</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} className="w-full p-3 border border-gray-300 rounded-lg" />
      <input value={author} onChange={(e) => setAuthor(e.target.value)} className="w-full p-3 border border-gray-300 rounded-lg" />
      <button onClick={updateBook} className="bg-yellow-600 text-white px-5 py-2 rounded-lg shadow hover:bg-yellow-700">Update Book</button>
    </div>
  );
}

function LoginPage({ setUserRole }) {
  const navigate = useNavigate();

  const login = (role) => {
    setUserRole(role);
    navigate("/");
  };

  return (
    <div className="h-screen flex flex-col justify-center items-center space-y-6 bg-gradient-to-br from-blue-100 to-white">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="text-4xl font-black text-blue-800"
      >
        Welcome to MyLibrary
      </motion.div>
      <button onClick={() => login("admin")} className="bg-blue-700 text-white px-6 py-2 rounded-full shadow-md hover:bg-blue-800">Login as Admin</button>
      <button onClick={() => login("user")} className="bg-green-600 text-white px-6 py-2 rounded-full shadow-md hover:bg-green-700">Login as User</button>
    </div>
  );
}

function LogoutButton({ setUserRole }) {
  const navigate = useNavigate();
  return (
    <div className="mt-10">
      <button
        onClick={() => {
          setUserRole(null);
          navigate("/login");
        }}
        className="bg-red-600 text-white px-4 py-2 rounded shadow hover:bg-red-700"
      >
        <LogOut className="inline mr-2" size={18} /> Logout
      </button>
    </div>
  );
}

export default function App() {
  const [userRole, setUserRole] = useState(null);
  const [books, setBooks] = useState(sampleBooks);

  return (
    <Router>
      {!userRole ? (
        <Routes>
          <Route path="*" element={<LoginPage setUserRole={setUserRole} />} />
        </Routes>
      ) : (
        <Layout>
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <HomePage userRole={userRole} books={books} setBooks={setBooks} />
                  <LogoutButton setUserRole={setUserRole} />
                </>
              }
            />
            {userRole === "admin" && (
              <>
                <Route path="/add-book" element={<AddBookPage books={books} setBooks={setBooks} />} />
                <Route path="/edit-book/:id" element={<EditBookPage books={books} setBooks={setBooks} />} />
              </>
            )}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </Layout>
      )}
    </Router>
  );
}
