// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [role, setRole] = useState('');
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

  const deleteBook = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  const borrowBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/borrow`);
    fetchBooks();
  };

  const buyBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/buy`);
    fetchBooks();
  };

  const returnBook = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/return`);
    fetchBooks();
  };

  const handleLogout = () => {
    setRole('');
  };

  if (!role) {
    return (
      <div className="container mt-5">
        <h2 className="text-center">Login as</h2>
        <div className="d-flex justify-content-center mt-4">
          <button className="btn btn-primary mx-2" onClick={() => setRole('admin')}>Admin</button>
          <button className="btn btn-secondary mx-2" onClick={() => setRole('user')}>User</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>{role === 'admin' ? 'Admin Panel' : 'Library Home'}</h2>
        <button className="btn btn-outline-danger" onClick={handleLogout}>Logout</button>
      </div>
      <div className="row">
        {books.map(book => (
          <div className="col-md-4 mb-3" key={book.id}>
            <div className="card h-100">
              <div className="card-body">
                <h5 className="card-title">{book.title}</h5>
                <p className="card-text">Author: {book.author}</p>
                <p className="card-text">Genre: {book.genre}</p>
                <p className="card-text">Status: {book.status}</p>
              </div>
              <div className="card-footer d-flex justify-content-between">
                {role === 'admin' ? (
                  <button className="btn btn-danger btn-sm" onClick={() => deleteBook(book.id)}>Delete</button>
                ) : (
                  <>
                    {book.status === 'available' && (
                      <>
                        <button className="btn btn-warning btn-sm" onClick={() => borrowBook(book.id)}>Borrow</button>
                        <button className="btn btn-success btn-sm" onClick={() => buyBook(book.id)}>Buy</button>
                      </>
                    )}
                    {book.status === 'borrowed' && (
                      <button className="btn btn-info btn-sm" onClick={() => returnBook(book.id)}>Return</button>
                    )}
                  </>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
