// frontend/src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { Button } from '@radix-ui/themes';
import '@radix-ui/themes/styles.css';

function Dashboard({ user }) {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '', id: null });
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    if (!form.title || !form.author || !form.genre) return;
    await axios.post('http://localhost:5000/books', form);
    setForm({ title: '', author: '', genre: '', id: null });
    fetchBooks();
  };

  const handleEdit = (book) => {
    setForm(book);
    setIsEditing(true);
  };

  const handleUpdate = async () => {
    await axios.put(`http://localhost:5000/books/${form.id}`, form);
    setForm({ title: '', author: '', genre: '', id: null });
    setIsEditing(false);
    fetchBooks();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  const handleBorrow = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/borrow`);
    fetchBooks();
  };

  const handleBuy = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/buy`);
    fetchBooks();
  };

  const handleReturn = async (id) => {
    await axios.patch(`http://localhost:5000/books/${id}/return`);
    fetchBooks();
  };

  const handleLogout = () => {
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Library Management</h1>
        <Button onClick={handleLogout} className="bg-red-500 text-white">Logout</Button>
      </div>

      {user === 'admin' && (
        <div className="mb-4 grid grid-cols-1 md:grid-cols-3 gap-2">
          <input name="title" value={form.title} onChange={handleChange} placeholder="Title" className="p-2 border rounded" />
          <input name="author" value={form.author} onChange={handleChange} placeholder="Author" className="p-2 border rounded" />
          <input name="genre" value={form.genre} onChange={handleChange} placeholder="Genre" className="p-2 border rounded" />
          <Button onClick={isEditing ? handleUpdate : handleAdd} className="col-span-1 md:col-span-3 w-full">
            {isEditing ? 'Update Book' : 'Add Book'}
          </Button>
        </div>
      )}

      <table className="min-w-full bg-white shadow rounded">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="p-3">Title</th>
            <th className="p-3">Author</th>
            <th className="p-3">Genre</th>
            <th className="p-3">Status</th>
            <th className="p-3">Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id} className="border-t">
              <td className="p-3">{book.title}</td>
              <td className="p-3">{book.author}</td>
              <td className="p-3">{book.genre}</td>
              <td className="p-3 capitalize">{book.status}</td>
              <td className="p-3 space-x-2">
                <Button onClick={() => handleBorrow(book.id)} disabled={book.status !== 'available'}>Borrow</Button>
                <Button onClick={() => handleBuy(book.id)} disabled={book.status !== 'available'}>Buy</Button>
                <Button onClick={() => handleReturn(book.id)} disabled={book.status === 'available'}>Return</Button>
                {user === 'admin' && <>
                  <Button onClick={() => handleEdit(book)}>Edit</Button>
                  <Button onClick={() => handleDelete(book.id)} className="bg-red-500">Delete</Button>
                </>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Login({ setUser }) {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    if (
      (username === 'admin' && password === 'admin') ||
      (username === 'user' && password === 'user')
    ) {
      setUser(username);
      navigate('/dashboard');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={handleLogin} className="bg-white p-6 rounded shadow w-80">
        <h2 className="text-xl font-bold mb-4 text-center">Login</h2>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          className="w-full p-2 border rounded mb-3"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="w-full p-2 border rounded mb-4"
        />
        <Button type="submit" className="w-full">Login</Button>
      </form>
    </div>
  );
}

export default function App() {
  const [user, setUser] = useState(null);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login setUser={setUser} />} />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/dashboard" element={<Dashboard user={user} />} />
      </Routes>
    </Router>
  );
}
